import React from "react";
import "./CrudButtons.css";

function CrudButtons({ onCreate, onRead, onUpdate, onDelete }) {
    return (
        <div style={containerStyle}>
            <button onClick={onCreate} className="button">
                Create
            </button>
            <button onClick={onRead} className="button">
                Read
            </button>
            <button onClick={onUpdate} className="button">
                Update
            </button>
            <button onClick={onDelete} className="button">
                Delete
            </button>
        </div>
    );
}

// Estilos para el contenedor de los botones
const containerStyle = {
    display: "flex",
    justifyContent: "center",
    gap: "2rem", // Modifica este valor para cambiar la separación entre botones
    margin: "2rem",
};

export default CrudButtons;
