import React from "react";
import { useNavigate } from "react-router-dom";
import "./CrudButtons.css";

function CrudButtons() {
    const navigate = useNavigate();

    const handleCreate = () => {
        navigate("/create");
    };

    const handleRead = () => {
        navigate("/read");
    };

    const handleUpdate = () => {
        navigate("/update");
    };

    const handleDelete = () => {
        navigate("/delete");
    };

    return (
        <div style={{ marginTop: '20px' }}>
            <button style={buttonStyle} onClick={() => window.location.href = '/create'}>Crear</button>
            <button style={buttonStyle} onClick={() => window.location.href = '/read'}>Leer</button>
            <button button style={buttonStyle} onClick={() => window.location.href = '/update'}>Actualizar</button>
            <button style={buttonStyle} onClick={() => window.location.href = '/delete'}>Eliminar</button>
    </div>
    );
}

// Estilos para el contenedor de los botones
const containerStyle = {
    display: "flex",
    justifyContent: "center",
    gap: "2rem", // Modifica este valor para cambiar la separación entre botones
    margin: "2rem",
};

export default CrudButtons;
