import React from "react";

function CrudButtons({ onCreate, onRead, onUpdate, onDelete }) {
    return (
        <div style={{ display: "flex", justifyContent: "center", gap: "1rem", margin: "2rem" }}>
            <button onClick={onCreate} style={buttonStyle}>
                Create
            </button>
            <button onClick={onRead} style={buttonStyle}>
                Read
            </button>
            <button onClick={onUpdate} style={buttonStyle}>
                Update
            </button>
            <button onClick={onDelete} style={buttonStyle}>
                Delete
            </button>
        </div>
    );
}

const buttonStyle = {
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
};

export default CrudButtons;
