import React from "react";

function CrudButtons({ onCreate, onRead, onUpdate, onDelete }) {
    return (
        <div style={containerStyle}>
            <button onClick={onCreate} style={buttonStyle}>
                Create
            </button>
            <button onClick={onRead} style={buttonStyle}>
                Read
            </button>
            <button onClick={onUpdate} style={buttonStyle}>
                Update
            </button>
            <button onClick={onDelete} style={buttonStyle}>
                Delete
            </button>
        </div>
    );
}

// Estilos para el contenedor de los botones
const containerStyle = {
    display: "flex",
    justifyContent: "center",
    gap: "2rem", // Modifica este valor para cambiar la separación entre botones
    margin: "2rem",
};

// Estilos para los botones
const buttonStyle = {
    width: "120px", // Ancho fijo
    height: "50px", // Alto fijo
    backgroundColor: "",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    textAlign: "center",
    display: "flex",
    alignItems: "center", // Centrar el texto verticalmente
    justifyContent: "center", // Centrar el texto horizontalmente
};

export default CrudButtons;
