import React from "react";
import CrudButtons from "./CrudButtons";
import "./App.css";

function App() {
    const onCreate = () => {
        console.log("Create action triggered");
        // Aquí puedes mostrar un formulario para crear un nuevo animal
    };

    const onRead = () => {
        console.log("Read action triggered");
        // Aquí puedes obtener la lista de animales o mostrar una tabla
    };

    const onUpdate = () => {
        console.log("Update action triggered");
        // Aquí puedes seleccionar un animal para actualizarlo
    };

    const onDelete = () => {
        console.log("Delete action triggered");
        // Aquí puedes seleccionar un animal para eliminarlo
    };

    return (
        <div>
            <h1 style={{ textAlign: "center" }}>Vida Silvestre</h1>
            <CrudButtons
                onCreate={onCreate}
                onRead={onRead}
                onUpdate={onUpdate}
                onDelete={onDelete}
            />
        </div>
    );
}
const headerStyle = {
  marginBottom: "2rem",
  color: "white", // Asegura que el título también sea blanco
};

const pageStyle = {
  backgroundColor: "#007BFF", // Azul de fondo
  minHeight: "100vh", // Asegura que el fondo cubra toda la pantalla
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  padding: "2rem",
  color: "white", // Color blanco para el texto general
};

export default App;
