import React, { useEffect, useState } from "react";
import api from "./api";
import ZooTable from "./ZooTable";
import CrudButtons from "./CrudButtons";

function App() {
    const [animals, setAnimals] = useState([]);

    useEffect(() => {
        api.get("/zoo")
            .then((response) => {
                setAnimals(response.data);
            })
            .catch((error) => {
                console.error("Error al obtener datos:", error);
            });
    }, []);

    const handleCreate = () => {
        console.log("Create button clicked");
    };

    const handleRead = () => {
        console.log("Read button clicked");
    };

    const handleUpdate = () => {
        console.log("Update button clicked");
    };

    const handleDelete = () => {
        console.log("Delete button clicked");
    };

    return (
        <div style={pageStyle}>
            <h1 style={headerStyle}>Animales del Zoológico</h1>
            <CrudButtons
                onCreate={handleCreate}
                onRead={handleRead}
                onUpdate={handleUpdate}
                onDelete={handleDelete}
            />
            <ZooTable animals={animals} />
        </div>
    );
}

// Estilo para el fondo azul de la página
const pageStyle = {
    backgroundColor: "#007BFF", // Azul de fondo
    minHeight: "100vh", // Asegura que el fondo cubra toda la pantalla
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "2rem",
    color: "white", // Color blanco para el texto general
};

// Estilo para el encabezado
const headerStyle = {
    marginBottom: "2rem",
    color: "white", // Asegura que el título también sea blanco
};


export default App;
