import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DeleteAnimalPage.css'; // Importa el archivo de estilos

const DeleteAnimalPage = () => {
    const [searchName, setSearchName] = useState('');
    const [searchId, setSearchId] = useState('');
    const [searchHabitat, setSearchHabitat] = useState('');
    const [selectedAnimal, setSelectedAnimal] = useState(null);

    const navigate = useNavigate();

    const handleDelete = () => {
        if (!selectedAnimal) {
            alert('Seleccione un animal para eliminar.');
            return;
        }

        // Lógica para eliminar el animal seleccionado
        console.log('Animal eliminado:', selectedAnimal);
        setSelectedAnimal(null);
    };

    return (
        <div className="delete-container">
            <h1 className="delete-title">Eliminar Animal</h1>
            <form className="delete-form">
                <div className="form-group">
                    <label htmlFor="searchName">Buscar Nombre:</label>
                    <input
                        type="text"
                        id="searchName"
                        value={searchName}
                        onChange={(e) => setSearchName(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="searchId">Buscar por ID:</label>
                    <input
                        type="text"
                        id="searchId"
                        value={searchId}
                        onChange={(e) => setSearchId(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="searchHabitat">Buscar por hábitat:</label>
                    <input
                        type="text"
                        id="searchHabitat"
                        value={searchHabitat}
                        onChange={(e) => setSearchHabitat(e.target.value)}
                    />
                </div>

                <div className="animal-list">
                    <span>Nombre</span>
                    <span>ID</span>
                </div>

                {/* Aquí podrías renderizar una lista de animales */}
                <div className="animal-list">
                    <span>Lola</span>
                    <span>123</span>
                </div>
            </form>

            <div className="delete-button-container">
                <button
                    type="button"
                    className="delete-button delete"
                    onClick={handleDelete}
                >
                    Eliminar
                </button>
                <button
                    type="button"
                    className="delete-button back"
                    onClick={() => navigate('/')}
                >
                    Atrás
                </button>
            </div>
        </div>
    );
};

export default DeleteAnimalPage;
