import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DeleteAnimalPage.css'; // Importa el archivo CSS

const DeleteAnimalPage = () => {
    const [searchField, setSearchField] = useState(''); // Campo de búsqueda seleccionado
    const [searchValue, setSearchValue] = useState(''); // Valor ingresado en el campo de búsqueda
    const [searchResults, setSearchResults] = useState([]); // Resultados de la búsqueda

    const navigate = useNavigate();

    const handleSearch = () => {
        if (!searchField || !searchValue) {
            alert('Seleccione un campo de búsqueda y escriba un valor.');
            return;
        }

        // Simulación de búsqueda en una base de datos o lista
        const allAnimals = [
            { name: 'Lola', id: '123', habitat: 'Marina' },
            { name: 'Felipe', id: '124', habitat: 'Selva' },
            { name: 'Toby', id: '125', habitat: 'Montaña' }
        ];

        const results = allAnimals.filter((animal) =>
            animal[searchField]?.toLowerCase().includes(searchValue.toLowerCase())
        );

        setSearchResults(results);
    };

    const handleDelete = (id) => {
        // Aquí puedes implementar la lógica para eliminar el animal con el ID proporcionado
        alert(`Animal con ID ${id} eliminado`);
        setSearchResults(searchResults.filter((animal) => animal.id !== id));
    };

    return (
        <div className="delete-container">
            <h1 className="delete-title">Eliminar Animal</h1>
            <form className="delete-form">
                <div className="form-group">
                    <label htmlFor="searchField">Campo de búsqueda:</label>
                    <select
                        id="searchField"
                        value={searchField}
                        onChange={(e) => setSearchField(e.target.value)}
                    >
                        <option value="">Seleccione un campo</option>
                        <option value="name">Nombre</option>
                        <option value="id">ID</option>
                        <option value="habitat">Hábitat</option>
                    </select>
                </div>

                <div className="form-group">
                    <label htmlFor="searchValue">Valor de búsqueda:</label>
                    <input
                        type="text"
                        id="searchValue"
                        value={searchValue}
                        onChange={(e) => setSearchValue(e.target.value)}
                    />
                </div>

                <button type="button" className="search-button" onClick={handleSearch}>
                    Buscar
                </button>
            </form>

            {searchResults.length > 0 && (
                <div className="result-list">
                    {searchResults.map((animal) => (
                        <div className="result-item" key={animal.id}>
                            <p>
                                <strong>Nombre:</strong> {animal.name} | <strong>ID:</strong>{' '}
                                {animal.id} | <strong>Hábitat:</strong> {animal.habitat}
                            </p>
                            <button
                                type="button"
                                className="delete-button"
                                onClick={() => handleDelete(animal.id)}
                            >
                                Eliminar
                            </button>
                        </div>
                    ))}
                </div>
            )}

            <div className="button-container">
                <button
                    type="button"
                    className="back-button"
                    onClick={() => navigate('/')}
                >
                    Atrás
                </button>
            </div>
        </div>
    );
};

export default DeleteAnimalPage;
