import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ReadAnimalPage.css';

const ReadAnimalPage = () => {
    const navigate = useNavigate();

    // Estados para habilitar/deshabilitar los campos
    const [isEditable, setIsEditable] = useState({
        name: false,
        id: false,
        date: false,
        habitat: false,
        caretaker: false,
    });

    // Función para manejar la habilitación de campos
    const handleEnableField = (field) => {
        setIsEditable((prevState) => ({
            ...prevState,
            [field]: true,
        }));
    };

    return (
        <div className="read-container">
            <h1 className="read-title">Buscar Animales</h1>

            <form className="read-form">
                <div className="form-group">
                    <button type="button" onClick={() => handleEnableField('name')}>
                        Buscar Nombre
                    </button>
                    <input
                        type="text"
                        placeholder="Ingrese nombre"
                        disabled={!isEditable.name}
                    />
                </div>

                <div className="form-group">
                    <button type="button" onClick={() => handleEnableField('id')}>
                        Buscar por ID
                    </button>
                    <input
                        type="text"
                        placeholder="Ingrese ID"
                        disabled={!isEditable.id}
                    />
                </div>

                <div className="form-group">
                    <button type="button" onClick={() => handleEnableField('date')}>
                        Buscar por Fecha
                    </button>
                    <input
                        type="date"
                        disabled={!isEditable.date}
                    />
                </div>

                <div className="form-group">
                    <button type="button" onClick={() => handleEnableField('habitat')}>
                        Buscar por Hábitat
                    </button>
                    <input
                        type="text"
                        placeholder="Ingrese hábitat"
                        disabled={!isEditable.habitat}
                    />
                </div>

                <div className="form-group">
                    <button type="button" onClick={() => handleEnableField('caretaker')}>
                        Buscar por Cuidador
                    </button>
                    <input
                        type="text"
                        placeholder="Ingrese cuidador"
                        disabled={!isEditable.caretaker}
                    />
                </div>
            </form>

            <button className="back-button" onClick={() => navigate('/')}>
                Atrás
            </button>
        </div>
    );
};

export default ReadAnimalPage;
